import React, {useState} from 'react';
import './index.css';
const App = ()=>{
    const [inputItem, setInputItem] = useState();
    const [ToDoList,setToDoList] = useState([]);

    const InputEvent = (event)=>{
       const value = event.target.value;
       setInputItem(value);
    }

    const AddToDoListItem = (event)=>{

        setToDoList((oldValues)=>{
            return [...oldValues,inputItem];
        });
        
        setInputItem("");
    }

    const deleteRow = (event)=>{
        
        console.log(event);
    }
    
    return (
        <>
            <div className='main_div'>
                <div className='center_div'>
                    <h1>ToDo List </h1>
                    <input type='text' placeholder='Add Item in List' name="" value={inputItem} onChange={InputEvent}/>
                    <button onClick={AddToDoListItem}>+</button>
                    <ol className=''>
                   
                        {ToDoList.map((inputVal,index)=>{
                            <ToDoList key={index} id={index} to_do={inputVal} onSelect={deleteRow}/>
                        })}

                    
                    </ol>

                </div>


            </div>
        </>
    );
}

export default App;
