import React, { useState } from 'react';
import './index.css';



const App = ()=>{

    const [FullName,setFullName] = useState({
        firstName : '',
        lastName : '',
        email : ''
    });

    const [data, setData] = useState();
    
    const InputEvent = (event)=>{
        const value = event.target.value;
        const name = event.target.value;
        console.log(value);
        if(name==="firstName"){
            setFullName((preVal)=>{
                return{
                    firstName : value,
                    lastName : preVal.lastName,
                    email : preVal.email
                }
            });
        }else if(name==="lastName"){

            setFullName((preVal)=>{
                return{
                    firstName : preVal.firstName,
                    lastName : value,
                    email : preVal.email
                }
            });

        }else if(name==="email"){

            setFullName((preVal)=>{
                return{
                    firstName : preVal.firstName,
                    lastName : preVal.lastName,
                    email : value
                }
            });

        }
    }


    const FormSubmit = (event)=>{
        event.preventDefault();
        console.log(FullName);
        setData("Your full name is : "+FullName.firstName+" "+FullName.lastName+" & Email is : "+FullName.email);


    }


    return (

        <>
            <form onSubmit={FormSubmit}>
                <div>
                    <h1>Data :  {data}</h1><br/>
                    <input type='text' name='firstName' placeholder='Enter Your First Name' onChange={InputEvent} value={FullName.firstName}/><br/>
                    <input type='text' name='lastName' placeholder='Enter Your Last Name' onChange={InputEvent}/><br/>
                    <input type='text' name='email' placeholder='Enter Your Email' onChange={InputEvent} /><br/>
                    <button>Submit</button>
                </div>
            </form>
        </>

    );
}

export default App;
