import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
// CSS working Area Start
var h1 = {
    border:'0px solid blue',
    display :'inline',
    backgroundColor:'lightblue',
    color:'green',
    borderRadius:'15px',
    padding:'15px',
    fontFamily: '"Josefin Sans", sans-serif'
}

// CSS Working Area End

// var hours = new Date().getHours();
var hours = 11;
if(hours>=1 && hours<=11){
    var greeting = "Good Morning";
}else if(hours>=12 && hours<=17){
    var greeting = "Good Afternoon";
}else if(hours>=18 && hours<=24){
    var greeting = "Good Night";
}


var img1 = "https://picsum.photos/300/200";
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <React.Fragment>
        {/* <h1 className='h1'>Mini Project : Greeting Website</h1> */}
        {/* <img src={img1} alt='random-img' style={{maxWidth:"100%", height:'100%'}}/> */}
        <div style={{textAlign:'center',marginTop:'250px'}}>
            <h1 style={h1}>
                Hello sir, <span style={{color:'black'}}>{greeting}</span>
            </h1>
        </div>
    </React.Fragment>
);
