import React, {useState} from 'react';
import './index.css';

const  App = ()=>{

    const [firstName,setFirstName] = useState();
    const [lastName,setLastName] = useState();

    const inputFirstName = (event)=>{
        setFirstName(event.target.value);
    }

    const inputLastName = (event)=>{
        setLastName(event.target.value);
    }


    const SubmitFunc = (event)=>{
        event.preventDefault();
        console.log(event);
        // setFirstName(event.target)
    }
    

    return(
    <>  
        <form onSubmit={SubmitFunc}>
            <div>
                    <h1>Hello {firstName} {lastName}</h1><br/>
                    <input type='text' placeholder='Please Enter first Name' value={firstName} onChange={inputFirstName} />&nbsp;&nbsp;<br/>
                    <input type='text' placeholder='Please Enter Last Name' value={lastName} onChange={inputLastName} />&nbsp;&nbsp;
                    <button>Submit</button>
            </div>
        </form>
    </>
    )
}

export default App;
