import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import './index.css';


const App = ()=>{
    const [field, setField] = useState({
        email : '',
        password : '',
    });

    const inputEvent = (event) =>{
        const name = event.target.name;
        const value = event.target.value;

        
            setField((preVal)=>{
                if(name = 'email'){
                    return{
                        email : value,
                        password : preVal.password,
                    }
                }else if(name='password'){
                    return {
                        email : preVal.email,
                        password : value
                    }  
                }
            });
        
    }


    return (
        <>
            <form onSubmit={{}}>
  <div class="form-group">
    <label for="exampleInputEmail1">Email address</label>
    <input onChange={inputEvent} type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" name="email"/>
    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Password</label>
    <input onChange={inputEvent} type="password" class="form-control" id="exampleInputPassword1" placeholder="Password" name="password"/>
  </div>

  <button type="submit" class="btn btn-primary">Submit</button>
</form>
        </>
    )
}

export default App;
