import React,{useState} from "react";

const App = ()=>{
    const [data,setData] = useState({
        firstName : '',
        lastName : '',
        email : ''
    });

    const [FullInfo,setFullInfo] = useState();

    const inputEvent = (event)=>{

        let {value,name} = event.target;
        console.log('event.target');
        console.log(event.target);

        setData(preVal=>{

            if(name==='firstName'){

                return {

                    firstName : value,
                    lastName : preVal.lastName,
                    email : preVal.email

                }
            }else if(name==='lastName'){
                    return {

                        firstName : preVal.firstName,
                        lastName : value,
                        email : preVal.email
        
                }
            }else if(name==='email'){
                    return {

                        firstName : preVal.firstName,
                        lastName : preVal.lastName,
                        email : value
        
                }
            }


        });
    }

    const onFormSub = (event)=>{
        event.preventDefault();
        setFullInfo("My name is "+data.firstName+" "+data.lastName+". And Email is "+data.email);
        
        
    }

    return (
        <>
            <form onSubmit={onFormSub}>
                <div>
                    <h1>Hello {FullInfo}</h1>
                    <input type="text" placeholder="Enter Your First Name" name="firstName" onChange={inputEvent} />
                    <br/>
                    <br/>
                    <input type="text" placeholder="Enter Your Last Name" name="lastName" onChange={inputEvent}/>
                    <br/>
                    <br/>
                    <input type="text" placeholder="Enter Your Email" name="email" onChange={inputEvent} autoComplete="off"/>
                    <br/>
                    <br/>
                    <button>Submit</button>
                </div>
            </form>
        </>
    )


}
export default App;
