console.log(time);

var actualDate = date.getDay()+"/"+date.getMonth()+"/"+date.getFullYear();
var root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <React.Fragment>
        <h1>Hello My name is { `${fullName}` }</h1>
        <p>Today Date is {actualDate}</p>
        <p>Current Time is : </p>
    </React.Fragment>
);
