import React from "react";
import {useState} from 'react';

const App = ()=>{

    const [NewfullName , setNewFullName] = useState();

    const [fullName, setFullName] = useState({
        firstName : "",
        lastName : "",
        email : ""
    });


    

    const submitForm = (event)=>{
        event.preventDefault();
        setNewFullName("My name is "+fullName.firstName+" "+fullName.lastName+" And Email is : "+fullName.email);
    }

    const InputEvent = (event)=>{
        let name = event.target.name;
        let value = event.target.value;

        setFullName((preVal)=>{

            return {
                ...preVal,
                [name] : value

            }
            
            // if(name==="firstName"){ 
            //     return {
            //         firstName : value,
            //         lastName : preVal.lastName,
            //         email : preVal.email
            //     }
            // }else if(name==="lastName"){
            //     return {
            //         firstName : preVal.firstName,
            //         lastName : value,
            //         email : preVal.email
            //     }
            // }else if(name==="email"){
            //     return {
            //         firstName : preVal.firstName,
            //         lastName : preVal.lastName,
            //         email : value
            //     }
            // }

        });

        

    }
    
    return (
        <>
            <form onSubmit={submitForm}>
                <div>
                    <h1>Hello {NewfullName}</h1>
                    <input type="text" name="firstName" value={fullName.firstName} onChange={InputEvent} placeholder="Enter Your First Name"/><br/><br/>
                    <input type="text" name="lastName" value={fullName.lastName} onChange={InputEvent} placeholder="Enter Your Last Name"/><br/><br/>
                    <input type="text" name="email" value={fullName.email} onChange={InputEvent} placeholder="Enter Your Email"/><br/><br/>
                    <button>Submit</button>
                </div>
            </form>

        </>
    );
}

export default App;
