import React from 'react';
import {useState} from 'react';
import './index.css';

const App = ()=>{
  const [value,setValue] = useState(0);

  const btnDecrement = ()=>{
    setValue(value-1);
    // setValue((oldValue)=>{
    //     return oldValue;
    // })
  }

  const btnIncrement = ()=>{
    setValue(value+1);
    // setValue((oldValue)=>{
    //   return oldValue;
    // })
  }

  return (
    <>
    <div className='main_div'>
        <div className='center_div'>
        <h1>{value}</h1>
        <button onClick={btnDecrement}>-</button>
        <button onClick={btnIncrement}>+</button>
        </div>
    </div>
    
    </>
  )
}

export default App;
