import React, { useState } from "react";
const Note = ()=>{
    const [note,setNote] = useState({
            notInput : '',
            noteTextarea : ''
    });
    
    const inputEvent = (event)=>{
       let inputValue =  event.target.value;
    }


    return (
        <>
            <div class="card" style={{width:'18rem',marginTop:'10px'}}>
                
                <div class="card-body">
                    <h5 class="card-title"><input placeholder="title" onChange={inputEvent}/></h5>
                    <textarea placeholder="Write Note..." style={{width:'100%'}}></textarea>
                    <a  href="#" class="btn btn-primary">+</a>
                </div>
            </div>
        </>
    )
}

export default Note;
