import React,{useState} from 'react';
import './index.css';
const App = ()=>{

    // -----------------------------------
    
    let bgColor = 'blue';

    const [btnName, setBgColor]  = useState('Click Me')
    const bgChange = ()=>{
        btnName = "Ohh !! ðŸ˜„";
        bgColor = "red";
        setBgColor(bgColor);
    }

    //------------------------------------
    return(
        <React.Fragment>

            <div style={{}}>
                <button onClick={bgChange} style={{backgroundColor:bgColor}}>{btnName}</button>
            </div>
            
            
           
        </React.Fragment>
    )   
}

export default App;
