import React, {useState} from 'react';
import '../index.css';

const  App = ()=>{
    const [name, setName] = useState();
    const [fullName,setFullName] = useState();

    const NameInputTag = (event)=>{
        setName(event.target.value);
    }

    const submitBtn = ()=>{
        setFullName(name);
    }


    return(
    <>
        <div>
            <h1>Hello {fullName}</h1>
            <input placeholder='Please Enter Your Name' onChange={NameInputTag} value={name}/>&nbsp;&nbsp;
            <button onClick={submitBtn}>Click Here</button>
        </div>
    </>
    )
}

export default App;
