import React,{useState} from 'react';
import './index.css';
const App = ()=>{

    //  events-----------------------------------
    let [btnName, setBtnNo]  = useState('Click Me');
    let [bgColor, setBgColor]  = useState('blue');
    const bgChange = ()=>{
        btnName = "Ohh !! ðŸ˜„";
        bgColor = "red";
        setBtnNo(btnName);
        setBgColor(bgColor);
    }

    const bgChangeDbClick = ()=>{
        btnName = "Ayee !! ðŸ˜ ";
        bgColor = "blue";
        setBtnNo(btnName);
        setBgColor(bgColor);
    }
 
    
    //------------------------------------
    return(
        <React.Fragment>

            <div>
                <button onClick={bgChange} onMouse onDoubleClick={bgChangeDbClick} style={{backgroundColor:bgColor}}>{btnName}</button>
            </div>
             
            
           
        </React.Fragment>
    )   
}

export default App;
