import React from "react";
import {useState} from 'react';
import './index.css';
import ToDoList from './ToDoList';
const App = ()=>{

        const [inputItem, setInputItem] = useState();
        const [arrToDoList,setArrToDoList] = useState([]);

        const itemEvents = (event)=>{
            let value = event.target.value;
            setInputItem(value);
        }
     
        const addItemBtn = (event)=>{
            setArrToDoList((oldValues)=>{
                return [...oldValues,inputItem];
            });
            setInputItem("");
           
        }

        
        const deleteFunction = (id)=>{
            arrToDoList((oldItems)=>{
                return oldItems.filter((arrElement,index)=>{
                    return index!==id;
                });
            });
        }

    return (
        <>
            <div className="main_div">
                <div className="center_div">
                <br/>
                <h1>ToDo List</h1>

                <input placeholder="Add a Items" type="text" onChange={itemEvents} value={inputItem}/>
                <button onClick={addItemBtn}>+</button>

                <ol>
                    
                    {arrToDoList.map((item,index)=>{
                       return( 
                        <ToDoList key={index} id={index} text={item} onSelect={deleteFunction}/>
                       )
                    })}
                </ol>

                </div>
            </div>
             

        </>
    );
}

export default App;
