import React from "react";
import {useState} from 'react';

const App = ()=>{

    const [firstName, setFirstName] = useState();
    const [lastName, setLastName] = useState();
    const [email, setEmail] = useState();

    const submitForm = (event)=>{
        event.preventDefault();

    }

    const InputEvent = (event)=>{
        console.log(event.target.name);
        let name = event.target.name;
        let value = event.target.value;

        if(name==="firstName"){

            setFirstName(value);

        }else if(name==="lastName"){
            setLastName(value);

        }else if(name==="email"){

            setEmail(value);
        }

    }
    
    return (
        <>
            <form onSubmit={submitForm}>
                <div>
                    <h1>Hello {firstName} {lastName} And Email : {email}</h1>
                    <input name="firstName" onChange={InputEvent} placeholder="Enter Your Name"/><br/><br/>
                    <input name="lastName" onChange={InputEvent} placeholder="Enter Your Name"/><br/><br/>
                    <input name="email" onChange={InputEvent} placeholder="Enter Your Name"/><br/><br/>
                    <button>Submit</button>
                </div>
            </form>

        </>
    );
}

export default App;
