import React, { useState } from "react";
import './style.css';
const CreateNote = ()=>{
    const [note,setNote] = useState({
            title : '',
            description : ''
    });

    const inputEvent = (event)=>{
       let {name,value} =  event.target

        setNote((preVal)=>{
            return {
                ...preVal,
                [name] : value
            }
        });

    }

    const onSubmitForm = (event) => {
        alert('hello');
    }
    
    return (
        <>
            <div className="" style={{marginLeft:'40%'}}>

                <div className="card" style={{width:'18rem',marginTop:'10px',alignItems:'center'}}>
                <br></br>
                <h4>Add Note</h4>
                    <div className="card-body">
                        <h5 className="card-title"><input placeholder="title" name='title' onChange={inputEvent}/></h5>
                        <textarea placeholder="Write Note..." name="description" style={{width:'100%'}} onChange={inputEvent}></textarea>
                        <button onClick={onSubmitForm}>+</button>
                    </div>
                </div>

                
            </div>
        </>
    )
}

export default CreateNote;
