import React from "react";
import './../index.css';

var heading1 = {
    textAlign:"center",
    color:'green',
    backgroundColor:'white',
    display:'inline',
    padding:'15px',
    borderRadius:"20px",
    marginLeft:"25%",
    marginTop:"25%"
}
var greeting = "";
var colorCss = "";
var Hours = new Date().getHours();
var Hours = 17;
if(Hours>=1 && Hours<12){
    greeting = "Good Morning";
    colorCss = "green";
}else if(Hours>=12 && Hours<18){
    greeting = "Good Afternoon";
    colorCss = "orange";
}else{
    greeting = "Good Night";
    colorCss = "cyan";
}

function Heading1(){
    return (
        
        <h1 style={heading1}>Hello Sir, <span style={{color:colorCss}}>{greeting}</span></h1>
        
    )
}

export default Heading1;
