import React, {useState} from 'react';
import './index.css';

const  App = ()=>{

    
    const [fullName,setFullName] = useState({
        fName : '',
        lName : ''
    });

    const inputEvent = (event)=>{
        const value = event.target.value;
        const name = event.target.name;
        
        console.log(value);
        console.log(name);
    }



    const SubmitFunc = (event)=>{
        event.preventDefault();
    }
    

    return(
    <>  
        <form onSubmit={SubmitFunc}>
            <div>
                    <h1>Hello  {fullName.fName}  {fullName.lName}</h1><br/>
                    <input style={{textAlign:'center'}} type='text' name='firstName' placeholder='Please Enter first Name'  onChange={inputEvent} />&nbsp;&nbsp;<br/>
                    <input style={{textAlign:'center'}} type='text' name='lastName' placeholder='Please Enter Last Name'  onChange={inputEvent} />&nbsp;&nbsp;
                    <button>Submit</button>
            </div>
        </form>
    </>
    )
}

export default App;
