import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import './index.css';


const App = ()=>{
    const [field, setField] = useState({
        email : '',
        password : '',
    });

    const inputEvent = (event) =>{
        let name = event.target.name;
        let value = event.target.value;
        
            setField((preVal)=>{
                if(name == 'email'){
                    return{
                        email : value,
                        password : preVal.password,
                    }
                }else if(name=='password'){
                    return {
                        email : preVal.email,
                        password : value
                    }
                }
            });
        
    }

    const onSubmit = ()=>{
        console.log(`Email is : ${field.email} and Password is : ${field.password} `);
        
    }
    return (
        <>
            
            <form onSubmit={()=>onSubmit} action="">
  <div className="form-group">
    <label >Email address</label>
    <input onChange={inputEvent} type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" name="email" value={field.email}/>
    <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
  </div>
  <div className="form-group">
    <label >Password</label>
    <input onChange={inputEvent} type="password" className="form-control" id="exampleInputPassword1" placeholder="Password" name="password" value={field.password}/>
  </div>

  <button type="submit" className="btn btn-primary">Submit</button>
</form>
        </>
    )
}

export default App;
