import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
// CSS working Area Start
var h1 = {
    border:'0px solid blue',
    display :'inline',
    backgroundColor:'#b6772b',
    color:'rgb(146, 70, 23)',
    borderRadius:'15px',
    padding:'15px',
    fontFamily: '"Josefin Sans", sans-serif'
}

// CSS Working Area End

var hours = new Date().getHours();
// var hours = 24;

if(hours>=1 && hours<12){
    var greeting = "Good Morning";
    var cssColor = "green";
}else if(hours>=12 && hours<17){
    var greeting = "Good Afternoon";
    var cssColor = "orange";
}else{
    var greeting = "Good Night";
    var cssColor = "black";
}



const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <React.Fragment>
        {/* <h1 className='h1'>Mini Project : Greeting Website</h1> */}
        {/* <img src={img1} alt='random-img' style={{maxWidth:"100%", height:'100%'}}/> */}
        <div style={{textAlign:'center',marginTop:'250px'}}>
            <h1 style={h1}>
                Hello sir, <span style={{color:'green'}}>{greeting}</span>
            </h1>
        </div>
    </React.Fragment>
);
